import {createContext} from 'react'
export const AuthContext=createContext({isLogedIn:false,
    UserId:null,
    Phone:null,
    Players:[],
    LOGIN:(id,phone,players)=>{
        UserId=id;
        Phone=phone;
        Players=players;
    }
    });
