import React,{useState} from 'react';

import ErrorModel from '../UIElements/Error'
import Card from '../UIElements/Card'
import LoadingSpinner from '../UIElements/LoadingSpinner'
import {VALIDATOR_REQUIRE, VALIDATOR_MINLENGTH} from '../FormElements/validater'
import Input from '../FormElements/Input'
import Button from '../FormElements/Button'
import {useForm} from '../Hook/Form-hook'

const Login=props=>{
    const [formstate,inputHandler,setFormData]=useForm(
        {
        phone:{
            value:'',
            isValid:false
        },
        otp:{
            value:'',
            isValid:true
        }
        },false);

    const [vissibleVerify,setVissibleVerify]=useState(false);

    return(
    <React.Fragment>
    <Card className="authentication">
        <h2>Continue with your phone</h2>
        <form>
            <Input
                element='input'
                id="phone"
                type="phone"
                label='Enter your valid phone number'
                value=''
                validators={[VALIDATOR_REQUIRE(),VALIDATOR_MINLENGTH(6)]}
                errorText="Please fill this with valid number"
                onInput={inputHandler}
            />
            <Button 
                type="submit" 
                onClick={(event)=>{
                    event.preventDefault();
                    setVissibleVerify(true);
                    console.log(formstate);
                 }}
                disabled={!formstate.isValid}>
                     SEND OTP
            </Button>
        </form>
        {vissibleVerify && <form>
            <Input
                element='input'
                id="otp"
                type="otp"
                label='Enter OTP you have recieved'
                value=''
                validators={[VALIDATOR_REQUIRE()]}
                errorText="Wrong OTP, please check again"
                onInput={inputHandler}
            />
            <Button 
                type="submit" 
                onClick={(event)=>{
                    event.preventDefault();
                    console.log(formstate);
                 }}>
                     Verify
            </Button>
        </form>}
    </Card>
    </React.Fragment>
    )
}

export default Login;
