import React, { useState } from 'react'
import Select from 'react-select'
import {useHistory} from 'react-router-dom'

import './findMatch.css'
import Input from 'react-select/src/components/input'
import Card from '../UIElements/Card'


const Signup=props=>{

  const options=[
    {value:'1',label:'One'},
    {value:'2',label:'Two'},
    {value:'3',label:'Three'},
    {value:'4',label:'Four'}
  ];

  const [selected,setSelected]=useState(null);
  
  const handleSelection=(selectedValue)=>{
    setSelected(selectedValue.value);
    console.log(selectedValue.value);
  }


  return(
    <React.Fragment>
      
      
        <form className="selector">
          <Select
           theme={theme => ({
            ...theme,
            borderRadius: 10,
            colors: {
              ...theme.colors,
              primary25: 'lightblue',
              primary: 'black',
            },
          })}
          placeholder="Select the number of players,you want to REGISTER"
          defaultValue={selected}
          onChange={handleSelection}
          options={options}/>
        </form>

          {selected && <Card className="authentication">
            <form>
              {selected>0 && <Input
               id="first"
               type="text"
               label='Enter First player name as per PUBG'
               value=''
               validators={[VALIDATOR_REQUIRE(),VALIDATOR_MINLENGTH(6)]}
               errorText="Enter the field as per PUBG account, otherwise you won't allow for match"
               onInput={inputHandler}/>}

              {selected>1 && <Input
               id="second"
               type="text"
               label='Enter Second player name as per PUBG'
               value=''
               validators={[VALIDATOR_REQUIRE(),VALIDATOR_MINLENGTH(6)]}
               errorText="Enter the field as per PUBG account, otherwise you won't allow for match"
               onInput={inputHandler}/>}

               {selected>2 && <Input
                id="third"
                type="text"
                label='Enter Third player name as per PUBG'
                value=''
                validators={[VALIDATOR_REQUIRE(),VALIDATOR_MINLENGTH(6)]}
                errorText="Enter the field as per PUBG account, otherwise you won't allow for match"
                onInput={inputHandler}/>}

                {selected>3 && <Input
                 id="fourth"
                 type="text"
                 label='Enter Fourth player name as per PUBG'
                 value=''
                 validators={[VALIDATOR_REQUIRE(),VALIDATOR_MINLENGTH(6)]}
                 errorText="Enter the field as per PUBG account, otherwise you won't allow for match"
                 onInput={inputHandler}/>}
            </form>
            </Card>}
   </React.Fragment>
  );
}

export default Signup;
