import React,{useState} from 'react';

import ErrorModel from '../UIElements/Error';
import firebase,{auth,provider} from './firebase.js';
import Card from '../UIElements/Card'
import LoadingSpinner from '../UIElements/LoadingSpinner'
import {VALIDATOR_REQUIRE, VALIDATOR_MINLENGTH} from '../FormElements/validater'
import Input from '../FormElements/Input'
import Button from '../FormElements/Button'
import {useForm} from '../Hook/Form-hook'

const Login=props=>{
    const [isLoading,setLoading]=useState(false);
    const [formstate,inputHandler,setFormData]=useForm(
        {
        phone:{
            value:'',
            isValid:false
        },
        otp:undefined
        },false);

    const [vissibleVerify,setVissibleVerify]=useState(false);

  const sendOTP=async (event)=>{
      event.preventDefault();
      setVissibleVerify(true);
      setFormData({
          ...formstate.inputs,
          phone:{
              ...formstate.inputs.phone,
              isValid:false
          },
          otp:{
              value:'',
              isValid:false
          }
      },false);
      setLoading(true);
      try{
          
    var recaptcha = new firebase.auth.RecaptchaVerifier('recaptcha');
    setLoading(false);
      }catch(err){console.log(err.message);
      }
    var number = '+917483048528';
    firebase.auth().signInWithPhoneNumber(number,recaptcha).then( function(e) {
      var code = prompt('Enter the otp', '');     
        if(code === null) return;      
        e.confirm(code).then(function (result) {
            console.log(result.user);
         }).catch(function (error) {
            console.error( error);
        });
    })
    .catch(function (error) {
        console.error( error);
    });
  }

    return(
    <React.Fragment>
    <Card className="authentication">
        <h2>Continue with your phone</h2>
        <form>
            <Input
                element='input'
                id="phone"
                type="phone"
                label='Enter your valid phone number'
                value=''
                validators={[VALIDATOR_REQUIRE(),VALIDATOR_MINLENGTH(6)]}
                errorText="Please fill this with valid number"
                onInput={inputHandler}
            />    
            <Button 
                type="submit" 
                onClick={sendOTP}
                disabled={!formstate.isValid}>
                     SEND OTP
            </Button>
            {isLoading && <LoadingSpinner asOverlay/>}
            
         <div id="recaptcha"></div>
        </form>
        {vissibleVerify && <form>
            <Input
                element='input'
                id="otp"
                type="otp"
                label='Enter OTP you have recieved'
                value=''
                validators={[VALIDATOR_REQUIRE()]}
                errorText="Wrong OTP, please check again"
                onInput={inputHandler}
            />
            <Button 
                type="submit" 
                onClick={(event)=>{
                    event.preventDefault();
                    console.log(formstate);
                 }}>
                     Verify
            </Button>
        </form>}
    </Card>
    </React.Fragment>
    )
}

export default Login;
